from PyQt5.QtGui import QPixmapfrom PyQt5.QtWidgets import QDialogfrom ui.about_ui import Ui_dlg_aboutclass AboutDialog(QDialog):    def __init__(self, parent=None):        super().__init__(parent)        self.ui = Ui_dlg_about()        self.ui.setupUi(self)        self.setFixedSize(self.size())        self.ui.label_4.setPixmap(QPixmap("img/icon.png"))    def set_version(self, version):        self.ui.lbl_version.setText("Version : {}".format(version))